mapper.delete:
DELETE 
		g
FROM tb_goods g INNER JOIN tb_member m
WHERE 
		g.g_code = #{goodsCode}
	AND m.m_id = #{memberId}
	AND m.m_pw = #{memberPw}

mapper.insert:
 		
 			SELECT 
 				CONCAT('goods_', MAX(CAST(SUBSTRING(g_code,7) AS DECIMAL)+1)) AS gCode 
			FROM tb_goods
 		
 		
 		INSERT INTO tb_goods(
			 g_code
			,m_id
			,g_name
			,g_cate
			,g_price
			,g_color
			,g_size
			,g_date
			,g_desc
		)VALUES(
			 #{goodsCode}
			,#{memberId}
			,#{goodsName}
			,#{goodsCate}
			,#{goodsPrice}
			,#{goodsColor}
			,#{goodsSize}
			,curdate()
			,#{goodsDesc}
		)
